import React, { useState } from 'react';
import StartButton from './components/StartButton';
import ProfileForm from './components/ProfileForm';
import ProfileDisplay from './components/ProfileDisplay';
import './App.css';

function App() {
  const [currentView, setCurrentView] = useState('start'); // 'start', 'form', 'display'
  const [profileData, setProfileData] = useState(null);

  const handleStartClick = () => {
    setCurrentView('form');
    setProfileData(null);
  };

  const handleFormSuccess = (data) => {
    setProfileData(data);
    setCurrentView('display');
  };

  const handleBackToStart = () => {
    setCurrentView('start');
    setProfileData(null);
  };

  return (
    <div className="App">
      {currentView === 'start' && (
        <StartButton onClick={handleStartClick} />
      )}
      
      {currentView === 'form' && (
        <div>
          <button
            onClick={handleBackToStart}
            className="m-4 px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
          >
            Back
          </button>
          <ProfileForm onSuccess={handleFormSuccess} />
        </div>
      )}
      
      {currentView === 'display' && (
        <div>
          <button
            onClick={handleBackToStart}
            className="m-4 px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
          >
            Back to Start
          </button>
          <ProfileDisplay profile={profileData} />
        </div>
      )}
    </div>
  );
}

export default App;
